using UnityEngine;
using System;
using System.Collections;
using UnityEngine.Audio;

namespace Assets.Scripts.Behaviour
{
	public class PauseGame: MonoBehaviour
	{
		private GameObject PauseWindow;
		private Boolean isPaused;

		//added by Elisa
		public AudioMixerSnapshot paused, unpaused;

		void Awake(){
//			PauseWindow = GameObject.FindGameObjectWithTag ("PauseWindow");
			PauseWindow = transform.FindChild ("PanelPause").gameObject as GameObject;
		}
		 
		void Start(){
			PauseWindow.SetActive (false);
			isPaused = false;
		}

		void Update(){
			if(Input.GetKeyDown(KeyCode.Escape))
			{
				if(!PauseWindow.activeInHierarchy && isPaused == false)
				{
					//added by Elisa 09.30
					isPaused = true;
					PauseWindow.SetActive(true);
					pauseGame();
					//commented out by Elisa 09.30
//					Time.timeScale = 0;
//					isPaused = true;
				}
				//added by Elisa 09.30
				else
				{
					Application.Quit();
				}
			}
		}

		//added by Elisa 09.30
		void pauseGame ()
		{
			Time.timeScale = Time.timeScale == 0 ? 1 : 0;
			lowPass();
		}

		//added by Elisa 09.30
		void lowPass ()
		{
			if (Time.timeScale == 0)
			{
				paused.TransitionTo (.01f);
			}
			else
			{
				unpaused.TransitionTo(.01f);
			}
		}

		public void OnCloseButtonClicked(){
			if (isPaused) {
				PauseWindow.SetActive (false);
				Time.timeScale = 1;
				isPaused = false;
			}
		}

		public void OnResumeButtonClicked(){
			OnCloseButtonClicked ();
		}

		public void OnReloadButtonClicked(){
			PauseWindow.SetActive (false);
			Time.timeScale = 1;
			Application.LoadLevel (Application.loadedLevel);
		}

		public void OnLevelSelectionButtonClicked(){
			Time.timeScale = 1;
			Application.LoadLevel("Level Selection");
		}
		public void OnPlayNextLevelButtonClicked()
		{
			Time.timeScale = 1;
			Application.LoadLevel(Application.loadedLevel + 1);
		}

		public void sampleCode(){
			if(!PauseWindow.activeInHierarchy && isPaused == false){
				PauseWindow.SetActive(true);
				Time.timeScale = 0;
				isPaused = true;
			}
		}

	}
}

